    """Self-translate: translate passage/question to English, then answer."""
    lang_name = LANG_NAMES.get(lang_code, lang_code)
    choices_str = "\n".join(f"{i+1}. {c}" for i, c in enumerate(choices))
    if lang_code == "eng":
